import React, { Component } from 'react';
import './TableArt.css'

class TableArt extends Component {
  delete = (id) => {
    document.getElementById(id).remove();
  }
  render() {
    let idRow = '';
    let contRow = 0;
    const tableDates = this.props.dates.map((dateRow) => {
        let cont = 0;

        contRow += 1;
        idRow = 'row0'+contRow;
        return(
        <tr id={idRow}>
          {  dateRow.map((date) => {
             cont += 1;
             return(
             <td>
                {date}
                {cont === 5 ? <a className="btn circle ired" onClick={this.delete(idRow)}>x</a>:null}
             </td> ) 
          }) }
        </tr> )
    });

    const tableFields = this.props.fields.map((field) => 
      <th>
        {field}
      </th>
    );

    return (
      <div className="tableArt">
        <table>
	      <thead>
	      <tr>
	      	{tableFields}
	      </tr>
	      </thead>
	      <tbody>
	        {tableDates}
	      </tbody>
        </table>
      </div>
    );
  }
}

export default TableArt;
